import React, { useState } from "react";
import Box from "./Box";
import './Container.css';
import AA from './AA/AA';
import MFI from './MFI/MFI';
import Pagination from "../Pagination/Pagination";
function Container(props) {
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 2;
    
    // Dummy data for testing
    const dummyData = [
        { id: 1, name: 'Item 1' },
        { id: 2, name: 'Item 2' },
        { id: 3, name: 'Item 3' },
        { id: 4, name: 'Item 4' },
        { id: 5, name: 'Item 5' },
        { id: 6, name: 'Item 6' },
        { id: 7, name: 'Item 7' },
        { id: 8, name: 'Item 8' },
        { id: 9, name: 'Item 9' },
        { id: 10, name: 'Item 10' },
    ];
    const handleAutoPagination = () => {
        setCurrentPage((prevPage) => {
          const nextPage = prevPage === totalPages ? 1 : prevPage + 1;
          return nextPage;
        });
      };
      useEffect(() => {
        const interval = setInterval(handleAutoPagination, 30000); // Trigger every 30 seconds
        return () => clearInterval(interval); // Clean up the interval on component unmount
      }, []);
    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = dummyData.slice(indexOfFirstItem, indexOfLastItem);

    const itirations = [1, 2, 3, 4, 5];

    return (
        <div>
            <div className="wholeContainer">
                <div className="container">
                    {currentItems.map((item) => (
                        <Box key={item.id} name={item.name} />
                    ))}
                </div>

                <div className="seccon">
                    <div className="AA">
                        <AA />
                    </div>
                    <div className="MFI">
                        <MFI />
                    </div>
                </div>

            </div>
            <div className="footer1">
                <div className="Pagination">
                    <Pagination
                        items={dummyData}
                        currentPage={currentPage}
                        itemsPerPage={itemsPerPage}
                        onPageChange={handlePageChange}
                    />
                </div>
            </div>
            <div className="footer2">
                <div className="indicator1">
                    <div className="circle1" style={{ "background": "rgb(100, 221, 23)" }}></div>
                    <div className="author1">
                        <p>Health Score &gt; 80%</p>
                    </div>
                    <div className="circle1" style={{ "background": "rgb(255, 193, 7)" }}></div>
                    <div className="author1">
                        <p>Health Score &gt; 50% &lt; 80%</p>
                    </div>
                    <div className="circle1" style={{ "background": "rgb(255, 87, 34)" }}></div>
                    <div className="author1">
                        <p>Health Score  &lt; 50%</p>
                    </div>
                    <div className="circle1" style={{ "border": "solid 1px black" }}></div>
                    <div className="author1">
                        <p>Health Score Not Available</p>
                    </div>
                </div>
            </div>
        </div>
    )

}
export default Container;
