    console.log("check" + accessToken);
    accessToken=`eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICIyY0NxRnpxRXJrNU5GS1RjTi1YSk1IdE9NS2tWVTZXS1hIdHZFMF8xZE5ZIn0..rpQ3HWOHhRAoFgv8WqDIjZZX_6muM3J17fPBZDhMT621l7tZZs1yldw7MVsCnmfFpBzoExhmxHlDBV3OKxsQQylhM4n1L34poGPN7x7DAFoE70u2t2YqOwroAH2tXcRgTbWbhQhznsGIvYNLGcsQSwiNX9aCKyfC6qSaD2n4vL6oIcZq8Z7OIVjnpBy2cfp_nMfYWTBkX_ZyMRCMf2yAEgKfgng7RsjQSWUUtONeonbBgIwmLqVbDLHP135XPG3rQ7z72JRjLcmLZyPtGpONMyZShUV6uIFcZe0ugB9mrOMEMBK_ZoiaKNVKBnb8kuO1F9Yd5fwe5MktOSK5GgkrAg`;
       const plantsResponse = await fetch(
      "https://api-idap.infinite-uptime.com/api/3.0/idap-api/plants/1868/machine-group-stats",
      {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: 'Bearer ' + JSON.parse(accessToken),
        },
      }
    );

    const plantsData = await plantsResponse.json();
    console.log("plantsdata", plantsData);

    return plantsData.data;
  } catch (error) {
    console.error(error);
    throw error;
  }
}
