export async function Plants() {
  let accessToken = "";

  const handleScriptLoad = async () => {
    console.log("hero");

    const data = await window.ZOHO.CREATOR.init();
    const config = {
      appName: "infinite-control-room",
      reportName: "All_Users",
      criteria: 'Username == "rapaka.chandrashekar@gmail.com"',
      page: "1",
      pageSize: "100",
    };

    return new Promise((resolve, reject) => {
      window.ZOHO.CREATOR.API.getAllRecords(config)
        .then(function (response) {
          let userData = response;
          accessToken = JSON.stringify(userData.data[0].Access_Token);
          console.log("userData" + JSON.stringify(userData.data[0].Access_Token));
          resolve(accessToken);
        })
        .catch(reject);
    });
  };

  accessToken = await handleScriptLoad();

  try {
    console.log("check" + accessToken);
    accessToken="eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICIyY0NxRnpxRXJrNU5GS1RjTi1YSk1IdE9NS2tWVTZXS1hIdHZFMF8xZE5ZIn0..IAH2V4jbY8QmOWliGwL0tgPICbV3izVxDJkAopTaQEo8M7l-G-vLWs_nOcLW0dMczx0PXhj_fzriaaNEn0VJMrmPc_yR-LFc-pQDSti52yuvRO1oRho3iqfqk5kVXV5g8p6lq2lm7CSUEZnsUJvxB8Ijv4BYShA0--yMJrvSx1g9QZxpQkl9Bx72kMQ818YPEa1xghmyvt-ILLq_s-qZgeNY16CDF97GAHM3qEG1iLhxnJODZGikbDzz1evCkFRo5gvr3k2MHX8fa1_B1IKAzjMREYnqW67HbJAqJ9pqwWYPslkPJkidyPQMwm61ZAiBF0t8s06KSR0ArUfBzn2aIg"
    const plantsResponse = await fetch(
      "https://api-idap.infinite-uptime.com/api/3.0/idap-api/plants/1868/machine-group-stats",
      {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: 'Bearer ' + JSON.parse(accessToken),
        },
      }
    );

    const plantsData = await plantsResponse.json();
    console.log("plantsdata", plantsData);

    return plantsData.data;
  } catch (error) {
    console.error(error);
    throw error;
  }
}
