import React, { useEffect, useState } from 'react';
import Header from './Components/Header';
import Container from './Components/Container';
import { Plants } from './Services/Json';


function App() {
  const [options, setOptions] = useState([]);
  const [plantID, setPlantID] = useState();
  const boxes = {};
  useEffect(() => {
    const script = document.createElement('script');
  
    script.src = "https://js.zohostatic.com/creator/widgets/version/1.0/widgetsdk-min.js";
    script.async = true;
  
    document.body.appendChild(script);
  
    return () => {
      document.body.removeChild(script);
    }
  }, []);
async function initialise() {
  console.log("inside zohoinitservices");
  const createrPromise = await window.ZOHO.CREATOR.init();
  console.log("required"+createrPromise);
  const recordOperations = window.ZOHO.CREATOR.API; // this is the promise, 
 console.log("required"+recordOperations);
  //setRecordOps(recordOperations); // and I'm parsing it to my Context API object to re-use it for every API call
}
initialise();
  function handleSelectChange(e) {
    setPlantID(e.target.value);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await Plants();
        const plantNames = response.map((ele) => ele.plantName);
        setOptions(plantNames);
        console.log(options);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  

  return (
    <div>
      <Header options={options} handleSelectChange={handleSelectChange} />
      <Container boxes={boxes} />
    </div>
  );
}

export default App;
