import React, { useEffect, useState } from 'react';
import Header from './Components/Header';
import Container from './Components/Container';
import { Plants } from './Services/Json';
import { LoadScript } from 'react-load-script';

function App() {
  const [options, setOptions] = useState([]);
  const [plantID, setPlantID] = useState();
  const boxes = {};

  function handleSelectChange(e) {
    setPlantID(e.target.value);
  }

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await Plants();
        const plantNames = response.map((ele) => ele.plantName);
        setOptions(plantNames);
        console.log(options);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const handleScriptLoad = () => {
    // Script loaded successfully, you can now use Zoho Creator widget SDK
    // Access ZC object to interact with Zoho Creator resources
    console.log(window.ZC);
    // Perform any additional initialization or actions with Zoho Creator SDK here
    // e.g., initialize Zoho Creator client, fetch records, etc.
  };

  return (
    <div>
      <Header options={options} handleSelectChange={handleSelectChange} />
      <Container boxes={boxes} />

      <LoadScript
        url="https://js.zohostatic.com/creator/widgets/version/1.0/widgetsdk-min.js"
        onLoad={handleScriptLoad}
      />
    </div>
  );
}

export default App;
